        $row = str_replace("{PROMEDIO}", round($promedio_trivias[0]->promedio_trivias), $row);
    }
    if ($promedio_trivias[0]->promedio_trivias > 0) {
        $row = str_replace("{PODERES}", "<img src='img/61_rf_badges_5.png' width='20'>", $row);
