        $row = str_replace("{PODERES}", "", $row);
    }
    $medallas = "";
    for ($k = 1; $k <= $datos_consolidados[0]->medallas; $k++) {
        $medallas .= "<img src='img/rf_medal.png'> ";
    }
    //$row = str_replace("{MEDALLAS}",$datos_consolidados[0]->medallas,$row);
    $row = str_replace("{MEDALLAS}", $medallas, $row);
    return ($row);
}
