function comprimir_pagina($buffer)
{
    $busca     = array(
        '/\>[^\S ]+/s',
        '/[^\S ]+\</s',
        '/(\s)+/s'
    );
    $reemplaza = array(
        '>',
        '<',
        '\\1'
    );
    return preg_replace($busca, $reemplaza, $buffer);
}
