function LimpiaRutFront($rut)
{
        //echo "<br>-> LimpiaRutFront $rut";
    $rut         = str_replace(".", "", $rut);
    $rut         = str_replace(",", "", $rut);
    $arreglo_rut = explode("-", $rut);
        //print_r($arreglo_rut);

    $rut         = intval(preg_replace('/[^0-9]+/', '', $arreglo_rut[0]), 10);
        // echo "rut $rut";
    return ($rut);
}
