    $porcentaje_avance_curso = (100 * $total_finalizados) / count($objetos);
    $PRINCIPAL               = str_replace("{PORCENTAJE_AVANCE_CURSO}", number_format($porcentaje_avance_curso, 0), $PRINCIPAL);
