                    $row_resp  = str_replace("{FECHA_RESPUESTA}", formatearFechaCompleta($resp->fecha) . "<br>" . $nuevaHora, $row_resp);
                }
                $row = str_replace("{BLOQUE_LISTADO_RESPUESTAS}", $row_resp, $row);
