function ObtenerCategoriaEvalJefe($nota, $requerido)
{
    $color_bajo   = "#e44c41";
    $color_cumple = "#88cd2a";
    $color_alto   = "#6adcfa";
    if ($nota < $requerido) {
        $arreglo[0] = "Bajo";
        $arreglo[1] = $color_bajo;
    } else if ($nota == $requerido) {
        $arreglo[0] = "Cumple";
        $arreglo[1] = $color_cumple;
    } else if ($nota > $requerido) {
        $arreglo[0] = "Sobre";
        $arreglo[1] = $color_alto;
    }
    if ($requerido <= 2) {
        $arreglo[2] = $color_bajo;
        $arreglo[3] = "Bajo";
    } else if ($requerido == 3) {
        $arreglo[2] = $color_cumple;
        $arreglo[3] = "Cumple";
    } else if ($requerido > 3) {
        $arreglo[2] = $color_alto;
        $arreglo[3] = "Sobre";
    }
    //Porcentaje
    return ($arreglo);
}
